/* global test, it, expect */
// @flow
import { parse } from "@orglnd/org-parse";
import prepareForHTML from "@orglnd/org-transform";
import render from "./html";
import testcases from "@orglnd/org-fixtures";

test.each(testcases)(`should parse %s`, input => {
  const tokens = parse(input);
  const document = prepareForHTML(tokens);
  const rendered = render(document, {});
  expect(rendered).toMatchSnapshot();
});
